mapper.select:
select

from tb_enterprise_user
where ENTERPRISE_ACCOUNT = #{username,jdbcType=VARCHAR} AND ENTERPRISE_PASSWORD =#{password,jdbcType=VARCHAR}

mapper.update:
update tb_enterprise_user
set REGISTRATION_LEGAL_PERSON = #{registrationLegalPerson,jdbcType=VARCHAR},
  POINTS_NOTE = #{pointsNote,jdbcType=VARCHAR}
where ENTERPRISE_ID = #{enterpriseId,jdbcType=VARCHAR}
